package com.h2kinfosys.learn.dayOne;

public class StoreManager {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Single line comment
		/* Multi line comment
		 */ 
		//byte 8 bits
		byte shoppingCartItems = 127;
		short billAmount = 30000;
		int salesPerDay = 99999999;
		long amazonSalesPerWeek = 999999999999l;
		
		//decimal point numbers
		float taxAmount = 99.34f;
		double totalTaxAmount = 100.78;
		
		//character variables
		char itemCategory = 'H';
		String name = "Niel Armstrong";
		
		//boolean variables
		boolean isPrefferedCustomer = true;
		
		
		 
		System.out.println("byte shoppingCartItems : " + shoppingCartItems);
		System.out.println("short billAmount : " + billAmount);
		System.out.println("int salesPerDay : " + salesPerDay);
		System.out.println("long amazonSalesPerWeek : " + amazonSalesPerWeek);
		System.out.println("float taxAmount : " + taxAmount);
		System.out.println("double totalTaxAmount : " + totalTaxAmount);
		System.out.println("char itemCategory : " + itemCategory);
		System.out.println("String name : " + name);
		System.out.println("boolean isPrefferedCustomer : " + isPrefferedCustomer);


	}

}
