package com.h2kinfosys.learn.dayOne;

public class WrapperExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte bytePrim = 127;
		Byte byteWrap = 127;
		
		int intPrim = 30000;
		Integer intWrap = 30000;
		
		System.out.println("byte wrapper Max = " + Byte.MAX_VALUE  + " Min = " + Byte.MIN_VALUE);
		System.out.println("Integer wrapper Max = " + Integer.MAX_VALUE  + " Min = " + Integer.MIN_VALUE);

	}

}
